import { Component, OnInit } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { LoginService } from '../../services/login.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {
  public loginForm!: FormGroup;

  constructor(private loginService: LoginService, private router: Router){}

  ngOnInit(): void {
      this.buildForm();
  }

  public buildForm(): void {
    this.loginForm = new FormGroup({
      email: new FormControl(null, [Validators.required, Validators.email]),
      password: new FormControl(null, [Validators.required]),
    });
  }

  public login(): void{

  }
}
<div class="container d-flex h-100 justify-content-center align-items-center ">
    <form class="container, form" [formGroup]="loginForm">
        <div class="heading">Sign In</div>
        <div class="form-group">
            <label for="email"></label>
            <input type="text" class="form-control input" id="email" formControlName="email" placeholder="E-mail" required>
            <div *ngIf="loginForm.get('email')?.invalid && (loginForm.get('email')?.dirty || loginForm.get('email')?.touched)" class="invalid-feedback">
                Insira um email válido.
            </div>
        </div>
        <div class="form-group">
            <label for="password"></label>
            <input type="password" class="form-control input " id="password" formControlName="password" placeholder="Password" required>
            <div *ngIf="loginForm.get('password')?.invalid && (loginForm.get('password')?.dirty || loginForm.get('password')?.touched)" class="invalid-feedback">
                A senha é obrigatória.
            </div>
            <span class="forgot-password"><a href="#">Forgot Password ?</a></span>
        </div>
        <button type="submit" class="btn btn-primary login-button" (click)="login()" [disabled]="!loginForm.valid">Sign In</button>
        <div class="social-account-container">
            <span class="title">Or Sign in with</span>
            <div class="social-accounts">
            
              
            </div>
        </div>
        <span class="agreement"><a href="#">Learn user licence agreement</a></span>      
    </form>
</div>
